sp_angular_provider.script:
function($rootScope, $timeout) {
    return {
        restrict: 'A',
        link: function(scope, el, attr) {
            scope.$watch(function() {
                    return {
                        width: el.width(),
                        height: el.height(),
                    }
                },
                function() {
                    scope.$emit(attr.elementResizedV2 + ':resized', el);
                },
                true //deep watch
            );
        }
    }
}
